  {name: "100 Barrel Series #41 Catamount Maple Wheat", description: "Catamount Maple Wheat is a full-bodied, smooth wheat ale made with genuine Vermont maple syrup. The original version of this beer was brewed in the winter of 2009 by Harpoon Vermont sales representative John Baker.", abv: "6.8", img: nil, avg_score: nil, user_id: nil, brewery_id: 3985, availability_id: nil, type_id: 112, brewery_db_id: "NiPIhq"},
